import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getQuestions } from "../redux/actions/getQuestions";
import { getAnswers } from "../redux/actions/getAnswers";
import { setScores } from "../redux/actions/setScores";
import { getPoints } from "../redux/actions/getPoints";
import { Redirect } from "react-router-dom";
import ProgressBar from "./ProgressBar";

function Quiz(props) {
  const {
    getQuestions,
    getAnswers,
    setScores,
    getPoints,
    questions,
    answers,
    points
  } = props;

  const [num, setNum] = useState(0);
  const [choiceScores, setChoiceScores] = useState([]);
  const [currentScores, setCurrentScores] = useState({
    fullstack: 0,
    ios: 0,
    android: 0,
    ux: 0,
    ds: 0
  });

  useEffect(() => {
    getQuestions();
    getAnswers();
    getPoints();
  }, []);

  useEffect(() => {
    setChoiceScores(points.filter(choice => choice.answer_id === num + 1));
  }, [num]);

  console.log({ choiceScores });

  if (num === questions.length) {
    setScores(currentScores);
    return <Redirect to="/results" />;
  }
  return (
    <div className="quiz-wrapper">
      <h1>Quiz</h1>
      <h2>{questions[num].question}</h2>

      <div className="flex flex-col">
        {answers.map(answer => {
          if (answer.question_id === questions[num].id) {
            return (
              <button
                key={answer.id}
                onClick={() => {
                  setNum(num + 1);
                }}
              >
                {answer.choice}
              </button>
            );
          }
        })}
      </div>
      <ProgressBar progress={`${questions[num].id}/${questions.length}`} />
    </div>
  );
}
const mapStateToProps = state => {
  return {
    questions: state.questions,
    answers: state.answers,
    points: state.points
  };
};

export default connect(mapStateToProps, {
  getQuestions,
  getAnswers,
  setScores,
  getPoints
})(Quiz);
