import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getQuestions } from "../redux/actions/getQuestions";
import { getAnswers } from "../redux/actions/getAnswers";
import { setScores } from "../redux/actions/setScores";
import { getTracks } from "../redux/actions/getTracks";
import { Redirect } from "react-router-dom";
import ProgressBar from "./ProgressBar";

function Quiz(props) {
  const {
    getQuestions,
    getAnswers,
    setScores,
    getTracks,
    questions,
    answers,
    Tracks
  } = props;

  const [num, setNum] = useState(0);
  const [currentAnswers, setCurrentAnswers] = useState([]);

  const [currentScores, setCurrentScores] = useState({
    fullstack: 0,
    ios: 0,
    android: 0,
    ux: 0,
    ds: 0
  });

  useEffect(() => {
    getQuestions();
    getAnswers();
    getTracks();
  }, []);

  useEffect(() => {
    setCurrentAnswers(
      answers.filter(answer => answer.question_id === questions[num].id)
    );
  }, [num]);

  // fullstack: "1.00"
  // iOS: "2.00"
  // android: "3.00"
  // UX: "4.00"
  // DS: "0.00"
  // id: 1
  // answer_id: 1

  if (num === questions.length) {
    setScores(currentScores);
    return <Redirect to="/results" />;
  }
  return (
    <div className="quiz-wrapper">
      <h1>Quiz</h1>
      <h2>{questions[num].question}</h2>

      <div className="flex flex-col">
        {answers.map(answer => {
          if (answer.question_id === questions[num].id) {
            return (
              <button
                key={answer.id}
                onClick={() => {
                  setNum(num + 1);
                  console.log(answer.id);
                }}
              >
                {answer.choice}
              </button>
            );
          }
        })}
      </div>
      <ProgressBar progress={`${questions[num].id}/${questions.length}`} />
    </div>
  );
}
const mapStateToProps = state => {
  return {
    questions: state.questions,
    answers: state.answers,
    Tracks: state.Tracks
  };
};

export default connect(mapStateToProps, {
  getQuestions,
  getAnswers,
  setScores,
  getTracks
})(Quiz);
