import React, { useState } from "react";
import { connect } from "react-redux";
import data from "./trackData";

function Fullstack(props) {
  const [selectedTrack, setSelectedTrack] = useState("fullstack");

  return (
    <div className="w-1/3 border">
      <h2>{data[selectedTrack].name}</h2>
      <p>{data[selectedTrack].description}</p>
    </div>
  );
}
const mapStateToProps = state => {
  return {
    scores: state.scores,
    tracks: state.tracks
  };
};
export default connect(mapStateToProps, {})(Fullstack);
