import React, { useState } from "react";
import { connect } from "react-redux";
import data from "./trackData";

function Fullstack({ scores }) {
  const [selectedTrack, setSelectedTrack] = useState("fullstack");
  const tracks = Object.keys(scores);
  const highest = Math.max.apply(
    Math,
    scores.map(function(o) {
      return o.y;
    })
  );
  console.log(highest);
  return (
    <div className="flex w-1/3 border">
      <ul>
        {tracks.map(track => (
          <li onClick={() => setSelectedTrack(track)}>{track}</li>
        ))}
      </ul>
      <div className="m-auto">
        <h2>{data[selectedTrack].name}</h2>
        <p>{data[selectedTrack].description}</p>
      </div>
    </div>
  );
}
const mapStateToProps = state => {
  return {
    scores: state.scores
  };
};
export default connect(mapStateToProps, {})(Fullstack);
