import React, { useState } from "react";

export default function ProgressBar({ progress }) {
  const [state, setState] = useState({ percentage: "1/2" });
  const style = `bg-red-600 h-full w-${state.percentage}`;

  return (
    <div className="relative h-4 w-3/4 border border-solid border-black rounded-lg">
      <div className={style} />
    </div>
  );
}
