import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getQuestions } from "../redux/actions/getQuestions";
import { getAnswers } from "../redux/actions/getAnswers";
import { Redirect } from "react-router-dom";

function Quiz(props) {
  const { getQuestions, getAnswers, questions, answers } = props;
  const [num, setNum] = useState(0);
  const [choices, setChoices] = useState([]);
  useEffect(() => {
    getQuestions();
    getAnswers();
  }, []);

  useEffect(() => {
    setChoices(
      answers.filter(choice => {
        if (choice.question_id === questions[num].id) {
          return choice.choice;
          // console.log(choice.choice);
        }
      })
    );
  }, [num]);

  if (num === questions.length) {
    return <Redirect to="/results" />;
  }
  // if (questions[num].question) {
  //   console.log(`question:${questions[num].id}`);
  //   answers.map(e => {
  //     if (e.question_id === questions[num].id) {
  //       return console.log(e.choice);
  //     }
  //   });
  // }
  console.log({ choices });
  return (
    <div className="quiz-wrapper">
      <h1>Quiz</h1>
      <h2>{questions[num].question}</h2>
      <button onClick={() => setNum(num + 1)}>Next</button>
    </div>
  );
}
const mapStateToProps = state => {
  return {
    questions: state.questions,
    answers: state.answers
  };
};

export default connect(mapStateToProps, { getQuestions, getAnswers })(Quiz);
