import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getQuestions } from "../redux/actions/getQuestions";

function Quiz(props) {
  useEffect(() => {
    console.log("getQuestions");
    props.getQuestions();
  }, []);
  const { questions, history } = props;
  const [num, setNum] = useState(0);
  console.log(questions.length);
  if (num === questions.length - 1) {
    alert("RESULTS PAGE");
    history.push("/results");
  }
  console.log(num);
  return (
    <div className="quiz-wrapper">
      <h1>Quiz</h1>
      <h2>{questions[num].question}</h2>
      <button onClick={() => setNum(num + 1)}>Next</button>
    </div>
  );
}
const mapStateToProps = state => {
  return {
    questions: state.questions
  };
};

export default connect(mapStateToProps, { getQuestions })(Quiz);
