import React, { useState } from "react";

export default function ProgressBar(props) {
  const [state, setState] = useState({ percentage: "1/4" });
  const style = `bg-red-600 h-full w-${state.percentage}`;
  const filler = props => <div className={style} />;
  return (
    <div className="relative h-4 w-3/4 border border-solid border-black rounded-lg">
      {filler}
    </div>
  );
}
